n = 200
p = [0.29, 0.2, 0.15, 0.135, 0.115, 0.07, 0.025, 0.015]
sv = 'abcdefgh'

counts = []
for i in p:
    counts.append(round(i * n))
print(*counts)
s = ""
for i in range(len(p)):
    s += sv[i] * counts[i]

print(f"Длина строки, удовлетворяющей требованиям {len(s)}","\n",s)

haff = ""


print(f"Длина Хаффмана {len(haff)}","\n",haff)
h = len(haff)
dic = {
    "a": "00",
    "b": "010",
    "c": "011",
    "d": "101",
    "e": "1100",
    "f": "1110",
    "g": "111101",
    "h": "1111110"
}

for keys in dic:
    s = s.replace(keys, dic[keys])

sh = len(s)
print(len(s), s)

print("ANSWER:\n", f"Х{sh-h}" if h < sh else f"Ш{h-sh}")
